import { DefaultTheme } from "styled-components";

export const lightTheme: DefaultTheme = {
    bgColor: "wheat",
    textColor: "black",
    cardTextColor: "black",
    accentColor: "orange",
    overviewBgColor: "#8C8C8C",
}

export const darkTheme: DefaultTheme = {
    bgColor: "#31373F",
    textColor: "wheat",
    cardTextColor: "black",
    accentColor: "orange",
    overviewBgColor: "#192b10",
}
